import React,{useState} from "react";
import "./style.css";
import axios from 'axios'

export default function AddUser() {

    const [data,setData]=useState({
        name:"",
        username:"",
        email:""
    })
    const handleChange =(event)=>{
        console.log(event)
        setData(()=>({
            ...data,[event.target.name]:event.target.value
        }))
    }
    const handleClick=async (e)=>{
        await axios.post("http://localhost:8001/Data" ,data);
    }
  return (
    <section className="bg-primary">
      <div className="container">
        <h3 className="text-center pt-5 text-white pb-5">
          Please Fill-Up the Details
        </h3>
        <form className="w-50 mx-auto p-4 border border-white text-white" onSubmit={(e)=>handleClick(e)} >
          <form>
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">
                Name
              </label>
              <input
                type="text"
                class="form-control"
                value={data.name}
                onChange={handleChange}
              />
            </div>
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">
                User Name
              </label>
              <input
                type="text"
                class="form-control"
                value={data.username}
                onChange={handleChange}
              />
            </div>
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">
                Email
              </label>
              <input
                type="email"
                class="form-control"
                value={data.email}
                onChange={handleChange}
              />
            </div>

            <button type="submit" className="submit-btn mt-3">
              Submit
            </button>
          </form>
        </form>
      </div>
    </section>
  );
}
