import React from 'react';
import { Handle, NodeProps, Position } from 'reactflow';
import defaultNodes from './DnDFlowPro'


export default () => {
  const onDragStart = (event, nodeType, label, nodedata,shapeName) => {
    event.dataTransfer.setData("application/reactflow", nodeType);
    event.dataTransfer.setData("label", label);
    event.dataTransfer.setData("nodedata", nodedata);
    event.dataTransfer.setData("shapeName",shapeName);
    event.dataTransfer.effectAllowed = "move";
    console.log(event, nodeType, label,shapeName);
  };
  
  return (
    <aside>
      <h5 className="offcanvas-label">Source</h5>
      <div className="dndnode"   onDragStart={(event) => onDragStart(event, "shape","Round Rectangle","data","round-rect")}  draggable>
      Round Rectangle
      </div>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, "shape","Diamond", "source_text","diamond")} draggable>
      Diamond
      </div>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, "shape","API", "source_api","round-rect")} draggable>
      hexagon
      </div>
      <h5 className="offcanvas-label">Processor</h5>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, "shape","voice Processer", "processor_node" ,"parallelogram")} draggable>
      circle
      </div>
      <div className="dndnode" onDragStart={(event) => onDragStart(event, "shape","text processer", "processor_node","parallelogram")} draggable>
      Text processer
      </div>
      <h5 className="offcanvas-label">Destination</h5>
      <div className="dndnode " onDragStart={(event) => onDragStart(event, "shape","Teams meeting", "destination","arrow-rect")} draggable>
      Teams meeting
      </div>
      <div className="dndnode " onDragStart={(event) => onDragStart(event, "shape","Outlook task", "destination","arrow-rect")} draggable>
      Outlook task
      </div>
      <div className="dndnode " onDragStart={(event) => onDragStart(event, "shape","Unit 4 task", "destination","arrow-rect")} draggable>
      Unit 4 task
      </div>
      <h5 className="offcanvas-label">Team</h5>
      <div className="dndnode " onDragStart={(event) => onDragStart(event, "shape","Team", "team")} draggable>
      Team
      </div>
      {/* <h5>Unit 4</h5>
      <div className="dndnode " onDragStart={(event) => onDragStart(event, 'shape',"Unit 4", "team")} draggable>
      Unit 4
      </div> */}
    </aside>
  );
};
