import React, { useEffect, useState } from 'react';
import ReactFlow, { useNodesState,  } from 'reactflow';
import 'reactflow/dist/style.css';

import './updatenode.css';

const initialNodes = [
  { id: '1', data: { label: '-' }, position: { x: 100, y: 100 } },
  { id: '2', data: { label: '-' }, position: { x: 100, y: 200 } },
];


const DemoUpdate = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);

  const [nodeName, setNodeName] = useState('Node 1');
  const [nodeNames,setNodeNames]=useState('node 2')

  useEffect(() => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === '1') {
         
          node.data = {
            ...node.data,
            label: nodeName,
          };
        }

        return node;
      })
    );
  }, [nodeName, setNodes]);

  useEffect(() => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === '2') {
         
          node.data = {
            ...node.data,
            label: nodeNames,
          };
        }

        return node;
      })
    );
  }, [nodeNames, setNodes]);

  

  return (
    <ReactFlow
      nodes={nodes}
      onNodesChange={onNodesChange}
      minZoom={0.2}
      maxZoom={4}
      attributionPosition="bottom-left"
    >
      <div className="updatenode__controls">
        <label>label:</label>
        <input value={nodeName} onChange={(evt) => setNodeName(evt.target.value)} />
        <br /><br />
        <label>label:</label>
        <input value={nodeNames} onChange={(evt) => setNodeNames(evt.target.value)} />

     
      </div>
    </ReactFlow>
  );
};

export default DemoUpdate;
