import React, { useState, useEffect } from "react";
import "./style.css";
import axios from "axios";

export default function Alldata() {
  // const [user, setUser] = useState([]);
  // console.log(user);
  // const getData = () => {
  //   axios
  //     .get("http://localhost:8001/Data")
  //     .then((response) => {
  //       const allUser = response.data;
  //       setUser(allUser);
  //     })
  //     .catch((error) => console.log(error));
  // };

  // useEffect(() => {
  //   getData();
  // }, []);


  const [user,setUser] =useState([]);

  useEffect(()=>{
      getData();
  },[]);

  const getData= async ()=>{
    const result=await axios.get("http://localhost:8001/Data");
      setUser(result)
  }


  return (
    <section className="bg-primary">
      <div className="container pb-5">
        <h3 className="text-center pt-5 text-white pb-5">All Information</h3>
        <table className="table text-white table-bordered border-white w-75 mx-auto">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Name</th>
              <th scope="col">User Name</th>
              <th scope="col">Email</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {user.map((value) => {
              return (
                <tr>
                  <th scope="row">{value.id}</th>
                  <td>{value.name}</td>
                  <td>{value.username}</td>
                  <td>{value.email}</td>
                  <td className="d-flex justify-content-evenly">
                    <button>Edit</button>
                    <button>Delete</button>
                    <button>View</button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </section>
  );
}
