import React, { useEffect, useState, useCallback } from 'react';
import ReactFlow, {
  useNodesState, addEdge,
  applyNodeChanges,
  applyEdgeChanges,
} from 'reactflow';
import 'reactflow/dist/style.css';

import './updatenode.css';

const initialNodes = [
  { id: '1', data: { label: 'NODE1' }, position: { x: 100, y: 100 } },
  { id: '2', data: { label: 'NODE2' }, position: { x: 100, y: 200 } },
  { id: '3', data: { label: 'NODE3' }, position: { x: 100, y: 300 } },

];
const initialEdges = [];

const DemoUpdate = () => {
  const [nodes, setNodes] = useNodesState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);
  const [nodeName, setNodeName] = useState(initialNodes);

  useEffect(() => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === nodeName.id) {
          node.data = {
            ...node.data,
            label: nodeName,
          };
        }

        return node;
      })
    );
  }, [nodeName, setNodes]);



  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    []
  );
  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);

  const updateState = (index) => (e) => {
    const newArray = nodes.map((item, i) => {
      if (index === i) {
        return { ...item, [e.target.name]: e.target.value };
      } else {
        return item;
      }
    });
    setNodes(newArray);
  };

  return (
    <ReactFlow
      onNodesChange={onNodesChange}
      onEdgesChange={onEdgesChange}
      nodes={nodes}
      onNodeClick={updateState}
      minZoom={0.2}
      edges={edges}
      maxZoom={4}
      onConnect={onConnect}
      attributionPosition="bottom-left"
    >
      <div className="updatenode__controls">
        <label>label:</label>
        {/* <input value={nodeName} onChange={(evt) => setNodeName(evt.target.value)} />
        <br /><br /> */}
       
        {nodes.map((datum, index) => {
        
            <input
              type="text"
              name="name"
              value={datum.name}
              onChange={updateState(index)}
            />
         
        })}
      </div>
    </ReactFlow>
  );
};

export default DemoUpdate;
