import React, { useState, useEffect } from "react";
import "./style.css";
import axios from "axios";
import {Link} from 'react-router-dom'
export default function Alldata() {
  const [user, setUser] = useState([]);
  

  const getData = async () => {
    const result = await axios.get("http://localhost:8001/Data");
    setUser(result.data);
  };

  useEffect(() => {
    getData();
  }, []);

  //delete user
  const deleteUser =async (id)=>{
      await axios.delete(`http://localhost:8001/Data/${id}`);
      getData();  //after delte data 
  }

  return (
    <section className="bg-primary">
      <div className="container pb-5">
        <h3 className="text-center pt-5 text-white pb-5">All Information</h3>

        <div className="row row-cols-1 row-cols-md-3 g-4">
          {user.map((value) => {
            return (
              <div className="col" key={value.id}>
                <div className="card">
                  <div class="card-body">
                    <h5 class="card-title">ID : {value.id}</h5>
                    <h5 class="card-text">Name: {value.name}</h5>
                    <h5 class="card-text">User Name: {value.username}</h5>
                    <h5 class="card-text">Email: {value.email}</h5>
                  </div>
                  <div className="d-flex justify-content-around pb-2">
                 <Link to={`/edituser/${value.id}`}><i class="bi bi-pencil-square"></i> </Link> 
                  <i class="bi bi-trash3-fill" onClick={()=>deleteUser(value.id)}></i>
                  <i class="bi bi-card-checklist"></i>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}
