import React, { useState, useRef, useCallback } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  useNodesState,
  useEdgesState,
  Controls,
} from 'reactflow';
import 'reactflow/dist/style.css';

import Sidebar from './Sidebar';

import './style.css';

const initialNodes = [];

let id = 0;
const getId = () => `dndnode_${id++}`;

const DnDFlow = () => {
  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);
  //2
  const [activeNode, setActiveNode] = useState();
  //3
  const [elements, setElements] = useState([]);
  //4
  const [newName, setNewName] = useState("");

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);
    
  const onDrop = useCallback(
    (event) => {
      event.preventDefault();

      const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
      const type = event.dataTransfer.getData('application/reactflow');

      if (typeof type === 'undefined' || !type) {
        return;
      }
      
      const position = reactFlowInstance.project({
        x: event.clientX - reactFlowBounds.left,
        y: event.clientY - reactFlowBounds.top,
      });
      const newNode = {
        id: getId(),
        type,
        position,
        data: { label: `${type} node` },
        
        
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [reactFlowInstance]
  );

 //1
 const updateNodeHandler = () => {
  if (!activeNode) return;
  setElements(
    elements.map((_current) => {
      if (_current.id === activeNode.id) {
        return {
          ..._current,
          data: { label: newName, id: _current.data.id }
        };
      }

      return _current;
    })
  );
};
  return (
    <div className="dndflow">
      <ReactFlowProvider>
      <div className="updatenode__controls">
        <label >label:</label>
        <input value={nodes} onChange={(evt) => setNodes(evt.target.value)}/>
        </div>
        <button type="button" onClick={updateNodeHandler}>
          Update
        </button>
        <div className="reactflow-wrapper" ref={reactFlowWrapper}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onInit={setReactFlowInstance}
            onDrop={onDrop}
            onDragOver={onDragOver}
            fitView
          >
            <Controls />
          </ReactFlow>
        </div>
        <Sidebar />
      </ReactFlowProvider>
    </div>
  );
};

export default DnDFlow;
